function showAlert()
{document.getElementById("alertMsg").style.display="block";}
function cshowAlert()
{document.getElementById("calertMsg").style.display="block";}
function hideAlert()
{document.getElementById("alertMsg").style.display="none";}
function chideAlert()
{document.getElementById("calertMsg").style.display="none";}
function hideCredentialErrorAlert()
{if(document.getElementById("loginCredentialsError"))
{document.getElementById("loginCredentialsError").style.display="none";}}
function validateLoginForm(){var sUsernameField=document.forms["loginForm"]["txtuserName"].value.trim();var sPasswordField=document.forms["loginForm"]["txtpassword"].value.trim();var flagVal=isValidEmail(sUsernameField);if(sUsernameField==null||sUsernameField=="")
{showAlert();document.getElementById("alertMsg").innerHTML="Enter User Name";return false;}
else if(!flagVal)
{showAlert();document.getElementById("alertMsg").innerHTML="Incorrect User Name";return false;}
else if(sPasswordField==null||sPasswordField=="")
{showAlert();document.getElementById("alertMsg").innerHTML="Enter password";return false;}}